import {useState,useEffect} from 'react';
import axios from 'axios';
const useCard = ()=>{
    const [imgArr,setimgArr] = useState([]);
    useEffect(()=>{
        axios({
            method : 'GET',
            url : 'https://pixabay.com/api/?key=16381049-c197cfa5caeabac8c93d8da2c&q=red+cars&image_type=photo',
            responseType : 'stream'
        })
        .then(response =>{
           // console.log(response);
            setimgArr(response.data);
        })
        .catch(err=>{
            console.log(err)
        })
    },[])
    return [imgArr];
}
export default useCard;
