import React, { useEffect } from "react";
import Styles from "./Bookmarks.module.css";
import Card from "../../../Card/Card";
export default function UserBookmarks(props) {
	const { bookmarks, isProfile, isUser, userId } = props;
	console.log(bookmarks);
	useEffect(() => {
		//FETCH FOR BOOKMAKRS -Fix this in user route!!
		//INSTEAD OF RETURING AN ARRAY CONATINING THE OBJECT IDS OF THE QUOTES,
		//RETURN THE QUTOES!!
		if (isProfile) {
			fetch("http://localhost:5000/bookmarks/profile", {
				method: "GET",
				headers: {
					"Content-Type": "application/json",
					Authorization: `Bearer ${localStorage.token}`,
				},
			})
				.then((res) => {
					res.json().then((data) => {
						console.log(data);
					});
				})
				.catch((err) => {
					console.log(err);
				});
		}
		if (isUser) {
			fetch(`http://localhost:5000/bookmarks/${userId}`, {
				method: "GET",
				headers: {
					"Content-Type": "application/json",
				},
			})
				.then((res) => {
					res.json().then((data) => {
						console.log(data);
					});
				})
				.catch((err) => {
					console.log(err);
				});
		}
	});
	return (
		<div className={Styles.contaier}>
			<h1>UserBookmarks</h1>
		</div>
	);
}
