import React, { useState, useRef } from "react";
import { Button, Modal, Form } from "react-bootstrap";
import Styles from "./AddQuote.module.css";
export default function AddQuote(props) {
	const [show, setShow] = useState(false);
	const [newQuote, setNewQuote] = useState({
		tittle: "",
		author: "",
		quote: "",
	});
	const AuthorInputRef = useRef();

	const handleClose = () => {
		setShow(false);
	};
	const handleSelectClick = (e) => {
		const selectValue = e.target.value;
		AuthorInputRef.current.value = selectValue;
	};

	return (
		<div>
			<Button onClick={() => setShow(true)}>Quote</Button>
			<Modal show={show} onHide={handleClose}>
				<div className={Styles.ModalBodyContainer}>
					<Form>
						<Form.Group>
							<Form.Control type="text" placeholder="Tittle" />
						</Form.Group>
						<Form.Group>
							<Form.Control
								ref={AuthorInputRef}
								type="text"
								placeholder="Author"
							/>
							<Form.Control as="select" onClick={handleSelectClick}>
								<option>Profile</option>
								<option>Anonymous</option>
							</Form.Control>
						</Form.Group>
						<Form.Group>
							<Form.Label>Example textarea</Form.Label>
							<Form.Control as="textarea" rows="3" />
						</Form.Group>
					</Form>
				</div>
			</Modal>
		</div>
	);
}
