import React, { useState, useRef } from 'react';
import {Button} from 'react-bootstrap';
import Styles from './Profile.module.css';
import AddLinkModal from './AddLinkModal/AddLinkModal';
import axios from 'axios';
//logic

const Profile = ()=>{
    const [showModal,setshowModal] = useState(false);
    const [DescriptionList,setDescriptionList] = useState([]);


    
    const handleClick = ()=>{
      
            axios.get('http://localhost:5000/descriptions')
            .then(function (response) {
            // handle success
            console.log(response.data[0].description);
            setDescriptionList(response.data);
            })
            .catch(function (error) {
            // handle error
            console.log(error);
            })
            .then(function () {
            // always executed
            console.log('reached here');
            });
            }
        
    
   

    return (
        <div className = {Styles.container}>
            <h1 className = {Styles.title}>No titles added yet</h1>
            <Button onClick = {()=> setshowModal(true)} variant="primary">+</Button>
            <AddLinkModal
                show = {showModal}
                onHide = {()=> setshowModal(false)}
            >
            </AddLinkModal>
            <Button onClick = {handleClick}>Show</Button>
            <div className = {Styles.description}></div>
            <div className = {Styles.description}></div>
            <div className = {Styles.description}></div>
            <div className = {Styles.description}></div>
            <div>DESCRIPTIONS</div>
            <ul>
                {DescriptionList.map((d)=>{
                    return <li key = {d._id}>{d.description}</li>
                })}
            </ul>
        </div>
    )
}
export default Profile;
