import React, {useRef,useState} from 'react';
import Styles from './Card.module.css';
import {Button} from '@material-ui/core';
import {Avatar} from '@material-ui/core'
const Card = (props)=>{
    const {src} = props;
    const [IsHovered, setIsHovered] = useState(false);
    const handlefocus = ()=>{
        setIsHovered(prevIsHovered=>{
            return !prevIsHovered;
        });
    }

    return (
        <div onMouseOver= {handlefocus} className = {Styles.Wrapper}>
                <img className = {Styles.container} src = {src} height = '200px'>
                </img>
                    <button className = {IsHovered ? Styles.GuardarButtonVisible : Styles.GuardarButton}>
                        GUARDAR
                    </button>
                    <Avatar className = {Styles.OptionsButton}>...</Avatar>
                   
        </div>
    )
}
export default Card;
