import React, { useState, useEffect } from "react";
import Styles from "./AnimatedQuote.module.css";
import Typical from "react-typical";
const makeArr1000s = (n) => {
	let arr = [];
	for (let i = 0; i < n; i++) {
		arr.push(1000);
	}
	return arr;
};
const insert1000s = (quote, arr1000s) => {
	let res = [];
	for (let i = 0; i <= quote.length - 1; i++) {
		res.push(quote[i]);
		res.push(arr1000s[i]);
	}
	return res;
};
const makeSteps = (string, n) => {
	let count = 0;
	let res = [];
	for (let i = 0; i < string.length - 1; i++) {
		if (string[i] === " ") {
			count++;
		}
		if (count === n) {
			res = string.slice(0, i);
		}
	}
	console.log(count);
	return res;
};
export default function AnimatedQuote(props) {
	const quoteText = "I love you, not only for what you are, but for what...".split(
		" "
	);
	let test = makeSteps(quoteText, 2);
	/* console.log(quoteText.length);
	const arr1000 = makeArr1000s(quoteText.length - 1);

	const test = insert1000s(quoteText, arr1000);
	test.pop(); */
	console.log(test);
	return (
		<div className={Styles.quote2}>
			<Typical steps={test} loop={1} wrapper="p" />
		</div>
	);
}
