import React, { useEffect, useState } from "react";
import QuotesGrid from "../../QuotesGrid/QuotesGrid";
import Card from "../../../Card/Card";
import Styles from "./UserQuotes.module.css";
import { date } from "joi";
export default function UsersQuotes(props) {
	// 1-> This is called when is logged in
	// 2-> This is called when is For an user

	const [quotes, setQuotes] = useState([]);
	useEffect(() => {
		console.log("I got called");
		if (IsProfile) {
			fetch("http://localhost:5000/user/quotes", {
				method: "GET",
				headers: {
					"Content-Type": "application/json",
					Authorization: `Bearer ${localStorage.token}`,
				},
			})
				.then((res) => {
					res.json().then((data) => {
						setQuotes(data.quotes);
					});
				})
				.catch((err) => {
					console.log(err);
				});
		} else if (isUser) {
			fetch("http://localhost:5000/user/quotes", {
				method: "GET",
				headers: {
					"Content-Type": "application/json",
					Authorization: `Bearer ${localStorage.token}`,
				},
			})
				.then((res) => {
					res.json().then((data) => {
						setQuotes(data.quotes);
					});
				})
				.catch((err) => {
					console.log(err);
				});
		}
	}, []);

	return (
		<div className={Styles.container}>
			{quotes.map((q, index) => {
				return (
					<Card
						key={index}
						centered={true}
						width="92%"
						height="100%"
						username={q.author}
						quote={`“${q.quote}”`}
						tag="Love"
						quoteTittle={q.title}
					></Card>
				);
			})}
		</div>
	);
}
