import React from 'react';
import Card from './Card/Card'
import Switch from './Switch/Switch';
import SearchBar from './SearchBar/SearchBar';
import {Button} from 'react-bootstrap';
import Styles from './ActiveDisplay.module.css';

//hook
import useActiveDisplay from './useActiveDisplay';
//Context
import ActiveDisplayContext from './ActiveDisplayContext';
import Panel from './Card/Panel/Panel';
const ActiveDisplay = ()=>{
    const [ActiveButton,setActiveButton] = useActiveDisplay();
    return (
        <div className = {Styles.container}>
            
            <ActiveDisplayContext.Provider value = {{ActiveButton,setActiveButton}}>
                 <Switch></Switch>
            </ActiveDisplayContext.Provider>
            
            {ActiveButton.search && <SearchBar></SearchBar>}
            {ActiveButton.profile && <Card></Card>}
            
                 
          
        </div>
    )
}
export default ActiveDisplay;
