import {useState,useEffect} from 'react';
import axios from 'axios';
const useCard = (URL)=>{
    const [imgArr,setimgArr] = useState([]);
    useEffect(()=>{
        axios({
            method : 'GET',
            url : URL,
            responseType : 'stream'
        })
        .then(response =>{
           // console.log(response);
            setimgArr(response.data.hits);
        })
        .catch(err=>{
            console.log(err)
        })
    },[])
    return [imgArr];
}
export default useCard;
