/* 
Mainly this is a general purpose Component for fetching data from the api.

*/
import React, { useState, useEffect, useContext, useRef } from "react";
import ImagesContext from "../../ImagesContext";
import axios from "axios";

export default function FetchImagesFromApi(props) {
	const { render, query, PageNumber } = props;
	const [Loading, setLoading] = useState(true);
	const [Err, setErr] = useState(false);
	const [ImgArr, setImgArr] = useState([]); // this may not be necessary
	const [hasMore, setHasMore] = useState(false); // this may not be necessary
	const { ImgArrContext, setImgArrContext } = useContext(ImagesContext);

	useEffect(() => {
		setImgArrContext(ImgArr);
	}, [ImgArr]);

	useEffect(() => {
		setImgArr([]);
	}, [query]);

	useEffect(() => {
		setLoading(true);
		let cancel;
		axios({
			method: "GET",
			url: `https://pixabay.com/api/?key=16381049-c197cfa5caeabac8c93d8da2c&q=${query}&image_type=photo&per_page=${21}&page=${PageNumber}`,
			cancelToken: new axios.CancelToken((c) => (cancel = c)),
		})
			.then((response) => {
				//FIX THIS REPETTITIOS
				console.log("Heloooo", response.data.hits);
				setImgArr((prevImgs) => {
					return [...new Set([...prevImgs, ...response.data.hits])];
				});
				setLoading(false);
			})
			.catch((err) => {
				//ignore everytime we cancel the request
				if (axios.isCancel(err)) {
					return;
				}
				setErr(true);
			});
		return () => cancel();
	}, [query, PageNumber]);

	return <div></div>;
}
