import {useState,createContext} from 'react';

const useActiveDisplay = ()=>{
    const [ActiveButton,setActiveButton] = useState({
        search : false,
        profile : true,
    });
    const ActiveDisplayContext = createContext({ActiveButton, setActiveButton});
    return ActiveDisplayContext;
}
export default useActiveDisplay;
