import React, { useState,useContext, useEffect } from 'react';
import Styles from './Home.module.css';
import {Spinner} from 'react-bootstrap';

//components
import Card from './Card/Card';
//hooks 
import useHome from './useHome';
//context
import SearchContext from '../../SearchContext';
const Home = ({history})=>{
    const {SearchString,setSearchString} = useContext(SearchContext);
    const [imgArr,Loading] =  useHome(`https://pixabay.com/api/?key=16381049-c197cfa5caeabac8c93d8da2c&q=${SearchString}&image_type=photo&per_page=21`);
    useEffect(()=>{
        console.log(SearchString);
    },[SearchString])
    return (
        <div className = {Styles.container}>
            {Loading &&
                <Spinner animation="border" role="status" size ='bg'>
                </Spinner>
            }
            <div className = {Styles.CardContainer}>
            {imgArr.map(img=>{
                return <Card key = {img.id} src = {img.largeImageURL}></Card>
            })}
            </div>  
        </div>      

    )
}
export default Home;
