import React, { useEffect, useState } from "react";
import { Modal, Form, Button, Container } from "react-bootstrap";
import { FormGroup } from "@material-ui/core";
import Styles from "./PasswordRecoveryModal.module.css";
import joi from "joi";
export default function ForgotPasswordModal(props) {
	const { show, onHide, closeLoginModal, setIsLoginOpen } = props;
	const [ShowSendEmailButton, setShowSendEmailButton] = useState(false);
	const [email, setEmail] = useState("");
	const schema = joi.object({
		email: joi.string().email({
			minDomainSegments: 2,
			tlds: { allow: ["com", "net", "org"] },
		}),
	});
	//Closes Password Recovery Modal and sets isLoginOpen to true, so it opens again
	const handleGoBackButton = () => {
		onHide();
		setIsLoginOpen(true);
	};
	//As it validates
	const handleChange = (e) => {
		setEmail(e.target.value);
	};
	const handleSubmit = () => {
		fetch("http://localhost:5000/password/reset", {
			method: "GET",
			body: email,
			headers: {
				"Content-Type": "application/json",
			},
		})
			.then((res) => {
				console.log(res);
			})
			.catch((err) => {
				console.log(err);
			});
	};
	//If show is true, close the login Modal
	useEffect(() => {
		if (show) {
			closeLoginModal();
		}
	}, [show]);

	useEffect(() => {
		const { error, value } = schema.validate({
			email: email,
		});
		if (error === null) {
			setShowSendEmailButton(true);
		} else {
			setShowSendEmailButton(false);
		}
	}, [email]);

	return (
		<div>
			<Modal show={show} onHide={onHide} centered>
				<Modal.Header>
					<Modal.Title>
						<h1 style={{ fontWeight: "bold" }}>Recover your account</h1>
					</Modal.Title>
				</Modal.Header>
				<div
					style={{
						width: "80%",
						padding: "10px",
						display: "grid",
						justifySelf: "center",
						height: "70px",
					}}
				>
					<p
						style={{
							fontWeight: "bold",
							fontSize: "16px",
							color: "grey",
							marginLeft: "7%",
						}}
					>
						You can use your email or username to recover your password
					</p>
				</div>

				<Modal.Body>
					<Form>
						<div className={Styles.FormInnerContainer}>
							<Form.Group
								onChange={handleChange}
								style={{ width: "90%", justifySelf: "center" }}
							>
								<Form.Control
									type="Email"
									placeholder="Enter your email"
									size="lg"
								></Form.Control>
							</Form.Group>
							<div
								style={{
									width: "90%",
									display: "flex",
									justifyContent: "flex-end",
								}}
							>
								<div
									style={{
										display: "flex",
										justifyContent: "space-between",
										width: "35%",
										padding: "2px",
									}}
								>
									<Button onClick={handleGoBackButton}>Go back</Button>
									<Button onClick={onHide}>Close</Button>
								</div>
							</div>
						</div>
					</Form>
				</Modal.Body>
				<Modal.Footer>
					{ShowSendEmailButton && (
						<Button className={Styles.SendEmailButton} onClick={handleSubmit}>
							Send email
						</Button>
					)}
				</Modal.Footer>
			</Modal>
		</div>
	);
}
