import React, { useEffect } from "react";
import { Modal, Form, Button, Container } from "react-bootstrap";
import { FormGroup } from "@material-ui/core";
import Styles from "./PasswordRecoveryModal.module.css";
import joi from "joi";
export default function ForgotPasswordModal(props) {
	const { show, onHide, closeLoginModal, setIsLoginOpen } = props;
	const [ShowSendEmailButton, setShowSendEmailButton] = useState();
	const schema = joi.object({
		email: joi.string().email({
			minDomainSegments: 2,
			tlds: { allow: ["com", "net", "org"] },
		}),
	});
	//If show is true, close the login Modal
	useEffect(() => {
		if (show) {
			closeLoginModal();
		}
	}, [show]);
	//Closes Password Recovery Modal and sets isLoginOpen to true, so it opens again
	const handleGoBackButton = () => {
		onHide();
		setIsLoginOpen(true);
	};
	//As it validates
	const handleChange = () => {};
	const handleSubmit = () => {};
	return (
		<div>
			<Modal show={show} onHide={onHide} centered>
				<Modal.Header>
					<Modal.Title>
						<h1 style={{ fontWeight: "bold" }}>Recover your account</h1>
					</Modal.Title>
				</Modal.Header>
				<div
					style={{
						width: "80%",
						padding: "10px",
						display: "grid",
						justifySelf: "center",
						height: "70px",
					}}
				>
					<p
						style={{
							fontWeight: "bold",
							fontSize: "16px",
							color: "grey",
							marginLeft: "7%",
						}}
					>
						You can use your email or username to recover your password
					</p>
				</div>

				<Modal.Body>
					<Form onSubmit={handleSubmit}>
						<div className={Styles.FormInnerContainer}>
							<Form.Group
								onChange={handleChange}
								style={{ width: "90%", justifySelf: "center" }}
							>
								<Form.Control
									type="Email"
									placeholder="Enter your email"
									size="lg"
								></Form.Control>
							</Form.Group>
							<div
								style={{
									width: "90%",
									display: "flex",
									justifyContent: "flex-end",
								}}
							>
								<div
									style={{
										display: "flex",
										justifyContent: "space-between",
										width: "35%",
										padding: "2px",
									}}
								>
									<Button onClick={handleGoBackButton}>Go back</Button>
									<Button onClick={onHide}>Close</Button>
								</div>
							</div>
						</div>
					</Form>
				</Modal.Body>
			</Modal>
		</div>
	);
}
