import React, { useContext, useRef, useCallback, useState } from "react";
import PageNumberContext from "../../PageNumberContext";
import LoadingContext from "../../LoadingContext";

export default function InfiniteScroll(props) {
	const { render } = props;
	/* const [imgNumber, setimgNumber] = useState(initialPageNumber); */
	const { PageNumber, setPageNumber } = useContext(PageNumberContext);
	const { Loading, setLoading } = useContext(LoadingContext);
	const observer = useRef();
	const lastImgElement = useCallback(
		(node) => {
			if (Loading) return;
			if (observer.current) observer.current.disconnect();

			observer.current = new IntersectionObserver((entries) => {
				console.log(entries);
				if (entries[0].isIntersecting) {
					console.log("visible");
					setPageNumber((PrevPageNumber) => PrevPageNumber + 1);
				}
			});
			if (node) {
				observer.current.observe(node);
			}
			console.log(node);
		},
		[Loading]
	);

	return <div>{render(lastImgElement)}</div>;
}
