import React, { useState, useEffect } from "react";
import Styles from "./MasonryGridLayout.module.css";
import { Button } from "react-bootstrap";
import AnimatedQuote from "../AnimatedQuote/AnimatedQuote";
import MyCard from "./myCard";
import { Slide, Paper } from "@material-ui/core";
import { Prev } from "react-bootstrap/PageItem";
export default function MasonryGridLayout() {
	const arr = [];
	const [show, setShow] = useState(true);
	const [enter, setEnter] = useState(false);
	setTimeout(() => {
		setShow(false);
	}, 5000);
	useEffect(() => {
		setEnter((Prev) => !Prev);
	}, [show]);
	return (
		<div className={Styles.container}>
			<div className={Styles.Wrapper}>
				{show && (
					<MyCard
						change={false}
						username="Romina"
						quote="“I love you, not only for what you are, but for what I am when I am with you.”"
						tag="Love"
						quoteTittle="Love for all"
					></MyCard>
				)}
				<MyCard
					change={false}
					username="Romina"
					quote="“Love is when the other person’s happiness is more important than your own.”"
					tag="Love"
					quoteTittle="Love for all"
				></MyCard>
				<MyCard
					change={false}
					username="Romina"
					quote="“Love is the crowning grace of humanity, the holiest right of the soul, the golden link which binds us.”"
					tag="Love"
					quoteTittle="Love for all"
				></MyCard>
				<MyCard
					change={false}
					username="Romina"
					quote="“Love is the crowning grace of humanity, the holiest right of the soul, the golden link which binds us.”"
					tag="Love"
					quoteTittle="Love for all"
				></MyCard>
				<MyCard
					change={false}
					username="Romina"
					quote="“Love is the crowning grace of humanity, the holiest right of the soul, the golden link which binds us.”"
					tag="Love"
					quoteTittle="Love for all"
				></MyCard>
				{/* <div className={Styles.quote1}>
					“I love you, not only for what you are, but for what...”
				</div> */}
				<div className={Styles.quote2}>
					“I love you, not only for what you are, but for what...”
				</div>
				<AnimatedQuote></AnimatedQuote>
				<Slide
					className={Styles.newQuote}
					direction="up"
					in={true}
					mountOnEnter
					unmountOnExit
				>
					<Paper elevation={4}>hola que tal</Paper>
				</Slide>
			</div>
		</div>
	);
}
