import {useState,useEffect,useContext, useRef} from 'react';
import SearchContext from '../../SearchContext'
import axios from 'axios';
const useHome = (query,url)=>{
 useEffect(()=>{
    axios({
        method : 'GET',
        url : url
    })
    .then(response=>{
        console.log(response.data.hits);
    })
 },[query,url])   
}
export default useHome;
