import React, {
	useEffect,
	useRef,
	useState,
	useContext,
	ReactDOM,
} from "react";
import Styles from "./myCard.module.css";
import VisibilityIcon from "@material-ui/icons/Visibility";
import FavoriteIcon from "@material-ui/icons/Favorite";
import BookmarkIcon from "@material-ui/icons/Bookmark";
import { Badge, Card } from "@material-ui/core";
import { Button } from "react-bootstrap";
import { number } from "joi";
import LastPositionStateContext from "../../Contexts/LastPositionStateContext";
export default function MyCard(props) {
	const { newquote, username, quoteTittle, quote, tag } = props;
	const { lastPosition, setLastPosition } = useContext(
		LastPositionStateContext
	); //Getting the state from the ref in props;
	const Cardref = useRef();

	useEffect(() => {
		return () => {
			let { x, y } = Cardref.current.getBoundingClientRect();
			x = Math.floor(x);
			y = Math.floor(y);
			let area = getComputedStyle(Cardref.current).gridArea.split("/");
			setLastPosition({
				x: x,
				y: y,
				boxArea: area.pop(),
			});
		};
	}, []);
	if (newquote) {
		console.log("Entering!!");

		let test = { transform: `translateX(${lastPosition.x}px)` };
		console.log({
			test,
		});
	}
	const style = { transform: "translateX(300px)" };
	return (
		<div
			ref={Cardref}
			style={newquote ? style : null}
			className={newquote ? Styles.newQuote : Styles.wrapper}
		>
			<div className={Styles.section1}>
				<p className={Styles.header}>{tag}</p>
				<h2 className={Styles.h2}>{quoteTittle}</h2>
				<p className={Styles.adjective}>{username}</p>
				<p className={Styles.quote}>{quote}</p>
			</div>

			<div className={Styles.section2}>
				<div className={Styles.bar}>
					<BookmarkIcon style={{ color: "#127ba3" }}></BookmarkIcon>
					<FavoriteIcon style={{ color: "#127ba3" }}></FavoriteIcon>
					<VisibilityIcon style={{ color: "#127ba3" }}></VisibilityIcon>
				</div>
			</div>
		</div>
	);
}
