import {useState} from 'react';
const useActiveDisplay = ()=>{
    //State for the current Active Button;
    const [ActiveButton,setActiveButton] = useState({
        search : false,
        profile : true,
    });
    return [ActiveButton,setActiveButton];
}
export default useActiveDisplay;
