import React, { useState, useEffect, useRef } from "react";
import Styles from "./MasonryGridLayout.module.css";
import { Button } from "react-bootstrap";
import AnimatedQuote from "../AnimatedQuote/AnimatedQuote";
import MyCard from "./myCard";
import LastPositionStateContext from "../../Contexts/LastPositionStateContext";
export default function MasonryGridLayout() {
	const [show, setShow] = useState(true);
	const [enter, setEnter] = useState(false);
	const [lastPosition, setLastPosition] = useState({
		x: "",
		y: "",
	});
	useEffect(() => {
		console.log(lastPosition);
	}, [lastPosition]);

	useEffect(() => {
		setTimeout(() => {
			setShow(false);
		}, 5000);
	}, []);
	return (
		<div className={Styles.container}>
			{console.log(LastPositionStateRef)}
			<div className={Styles.Wrapper}>
				{show && (
					<LastPositionStateContext.Provider
						value={{ lastPosition, setLastPosition }}
					>
						<MyCard
							change={false}
							username="Romina"
							quote="“I love you, not only for what you are, but for what I am when I am with you.”"
							tag="Love"
							quoteTittle="Love for all"
						></MyCard>
					</LastPositionStateContext.Provider>
				)}
				<LastPositionStateContext.Provider
					value={{ lastPosition, setLastPosition }}
				>
					<MyCard
						change={false}
						username="Romina"
						quote="“I love you, not only for what you are, but for what I am when I am with you.”"
						tag="Love"
						quoteTittle="Love for all"
					></MyCard>
				</LastPositionStateContext.Provider>
				<LastPositionStateContext.Provider
					value={{ lastPosition, setLastPosition }}
				>
					<MyCard
						change={false}
						username="Romina"
						quote="“I love you, not only for what you are, but for what I am when I am with you.”"
						tag="Love"
						quoteTittle="Love for all"
					></MyCard>
				</LastPositionStateContext.Provider>
				<LastPositionStateContext.Provider
					value={{ lastPosition, setLastPosition }}
				>
					<MyCard
						change={false}
						username="Romina"
						quote="“I love you, not only for what you are, but for what I am when I am with you.”"
						tag="Love"
						quoteTittle="Love for all"
					></MyCard>
				</LastPositionStateContext.Provider>
				<LastPositionStateContext.Provider
					value={{ lastPosition, setLastPosition }}
				>
					<MyCard
						change={false}
						username="Romina"
						quote="“I love you, not only for what you are, but for what I am when I am with you.”"
						tag="Love"
						quoteTittle="Love for all"
					></MyCard>
				</LastPositionStateContext.Provider>

				{/* <div className={Styles.quote1}>
					“I love you, not only for what you are, but for what...”
				</div> */}
				<div className={Styles.quote2}>
					“I love you, not only for what you are, but for what...”
				</div>
				<AnimatedQuote></AnimatedQuote>
				{!show && (
					<MyCard
						LastPositionStateRef={LastPositionStateRef}
						newquote={true}
						username="Romina"
						quote="“Love is the crowning grace of humanity, the holiest right of the soul, the golden link which binds us.”"
						tag="Love"
						quoteTittle="Love for all"
					></MyCard>
				)}
			</div>
		</div>
	);
}
