import React, { useState,useContext, useEffect, useRef, useCallback } from 'react';
import Styles from './Home.module.css';
import {CircularProgress} from '@material-ui/core';

//components
import Card from './Card/Card';
//hooks 
import useHome from './useHome';
//context
import SearchContext from '../../SearchContext';
const Home = ({history})=>{
    const {SearchString,setSearchString} = useContext(SearchContext);
    const [imgNumber,setimgNumber] = useState(21);
    const {Loading,err,ImgArr,hasMore} = useHome(SearchString,imgNumber);
    const observer = useRef();
    const lastImgElement = useCallback((node)=>{
        console.log(node);
    })
    console.log(ImgArr)
    
    return (
        <div className = {Styles.container}>
            {Loading &&
               <div className = {Styles.loadingContainer}>
                    <h1 className ={Styles.Loading}>Loading...</h1>
               </div>        
            }
            <div className = {Styles.CardContainer}>
            {ImgArr.map((img,index)=>{
                if(ImgArr.length === index +1){
                    return <Card  key = {img.id} src = {img.largeImageURL}></Card>
                }else{ 
                    return <Card key = {img.id} src = {img.largeImageURL}></Card>
                }
               
            })}
            </div>   
        </div>      

    )
}
export default Home;
