import React, { useState } from 'react'
import Styles from './Pin.module.css';

import PinImg from './PinImg/PinImg';
import PinPanel from './PinPanel/PinPanel';
 const Pin = (props)=> {
    const {history} = props;
    const [IsMouseOverPin,setIsMouseOverPin] = useState(false);
    const handleclick = ()=>{
        if(!IsMouseOverPin){
            history.push({
                pathname : '/'
            });
        }   
    }

    return (
            <div onClick = {handleclick} className = {Styles.Wrapper}>
                <div onMouseLeave = {()=>{setIsMouseOverPin(false)}} 
                     onMouseEnter = {()=> {setIsMouseOverPin(true)}} 
                     className = {Styles.pin}>

                    <PinImg src = {props.location.state.src}></PinImg>
                    <PinPanel></PinPanel>
                </div>
            </div>
            
        
    )
}
export default Pin;
