import React from 'react';
import {Formik} from 'formik';
import {Form,FormGroup,Button} from 'react-bootstrap';
import * as Yup from 'yup';
const validationSchema = Yup.object().shape({
    name : Yup.string()
    .min(1,'must have at leat one character')
    .max(255,'no more than 255 characters are allowed')
    .required('must enter name'),
    description : Yup.string()
    .min(15,'must be at least 15 characters long')
    .max(255,'description should not be longer than 255 characters')
    .required('must enter a description')
});
const FormikForm = ()=>{
    return (
        
            <Formik 
            initialValues ={{name : '', description : ''}}
            validationSchema = {validationSchema}
            >
            {({values,errors,touched,handleBlur,handleChange,handleSubmit,handleReset})=>{
               return (
                <Form>
                <Form.Group>
                    <Form.Label>Group name</Form.Label>
                        <Form.Control 
                        type="text" 
                        placeholder="Enter name" 
                        onChange = {handleChange} 
                        onBlur = {handleBlur} 
                        value ={values.name}/>
                        <Form.Text className="text-muted">
                            This name well be showed to other users.
                        </Form.Text>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Group description</Form.Label>
                        <Form.Control 
                        type = 'text' 
                        as="textarea" 
                        rows="3" 
                        onChange = {handleChange}
                        onBlur = {handleBlur}
                        value = {values.description}
                        />
                        <Form.Text className="text-muted">
                        Short description of what your group is about.
                        </Form.Text>
                </Form.Group>
                    <Button variant="primary" type="submit">
                    Save
                    </Button>
            

                </Form>
               )
              

            }}
            </Formik>
              
    
                
    )
}
export default FormikForm;
