		onHide();
		setIsLoginOpen(true);
	};
	return (
		<div>
			<Modal show={show} onHide={onHide} centered>
				<Modal.Header>
					<Modal.Title>Recover you account</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<h1>This is the body</h1>
				</Modal.Body>
				<Modal.Footer>
					<Button onClick={onHide}>Go back</Button>
					<Button onClick={onHide}>Close</Button>
				</Modal.Footer>
			</Modal>
		</div>
	);
}
