import React,{useState} from 'react'
import {Tabs} from 'react-bootstrap';
import Styles from './Tab.module.css';
export default function Tab() {
    const [isFotosActive, setisFotosActive] = useState(false);
    const [isComentariosActive, setisComentariosActive] = useState(false);

    return (
        <div>
            <button onClick = {()=> setisFotosActive(!isFotosActive)} className = {Styles.Fotos}>Fotos</button>
            <button onClick = {()=> setisComentariosActive(!isComentariosActive)} className = {Styles.Comentarios}>Comentarios</button>
            <div className= {Styles.DisplayArea}>
                {isFotosActive && <h1>Fotos</h1>}
                {isComentariosActive && <h1>Comentarios</h1>}

            </div>
        </div>
    )
}
