import React, { useState } from "react";
import Styles from "./Homev2.module.css";
import QuotesGrid from "./QuotesGrid/QuotesGrid";
import { BrowserRouter, Route } from "react-router-dom";
import Profile from "../Profile/Profile";
import NavList from "./NavList/NavList";
import NavListContext from "../../Contexts/NavListContext";
export default function Homev2() {
	const [selectedIndex, setSelectedIndex] = useState(0);

	return (
		<div className={Styles.Container}>
			{console.log(selectedIndex)}
			<BrowserRouter>
				{/*The navbar renders for every route!*/}
				<div className={Styles.Items}>
					<NavListContext.Provider value={{}}>
						<Route
							path="/"
							component={() => {
								return (
									<NavList
										index={{ selectedIndex, setSelectedIndex }}
									></NavList>
								);
							}}
						></Route>
					</NavListContext.Provider>
				</div>
				<div className={Styles.CenterColumn}>
					<Route
						path="/profile"
						exact
						component={() => {
							return <Profile selected={selectedIndex === 1}></Profile>;
						}}
					></Route>

					<Route
						path="/home"
						exact
						component={() => {
							return <QuotesGrid selected={selectedIndex === 0}></QuotesGrid>;
						}}
					></Route>
				</div>
				<div className={Styles.Items}></div>
			</BrowserRouter>
		</div>
	);
}
