import React,{useContext} from 'react';
import {Button} from 'react-bootstrap';
//Styles
import Styles from './Switch.module.css';
//hook-Logic
import useSwitch from './useSwitch';
//Context
import ActiveDisplayContext from '../ActiveDisplayContext';
const Switch = ()=>{
    const [ActiveButton,ButtonRef] = useSwitch();
    let current;
    if (ActiveButton.search){
        current = 'search';
    }else{
        current = 'profile';
    }
    return (
        <Button className = {Styles.button} ref = {ButtonRef} variant = 'dark'>{current}</Button>
    )
}
export default Switch;
